USE bdTransito

--Quantos motoristas existem no banco de dados
SELECT COUNT(tbMotorista.idMotorista) 'Quantidade de Motorista'FROM tbMotorista

--Quantos motoristas possuem Silva no sobrenome
SELECT COUNT(tbMotorista.nomeMotorista) 'Quantidade de Motoristas que possuem Silva' FROM tbMotorista
	WHERE tbMotorista.nomeMotorista LIKE '%Silva'

--Quantos motoristas nasceram no ano 2000SELECT COUNT(tbMotorista.dataNascimentoMotorista) 'Quantidade de Motorista que nasceram no ano 2000' FROM tbMotorista	WHERE tbMotorista.dataNascimentoMotorista = '2000'--Quantos motoristas possuem mais de 10 pontos de pontuação acumuladaSELECT COUNT(tbMotorista.pontuacaoAcumulada) 'Quantidade de Motoristas que possuem mais de 10 pontos acomulado'FROM tbMotorista	WHERE tbMotorista.pontuacaoAcumulada>10--A somatória da pontuação de todos os motoristasSELECT SUM(tbMotorista.pontuacaoAcumulada) 'Somatória de pontuação de Todos os Motoristas' FROM tbMotorista--A média de pontuação de todos os motoristasSELECT AVG(tbMotorista.pontuacaoAcumulada) 'Média de Pontuação de todos os Motoristas' FROM tbMotorista--Quantos veículos o motorista de código 1 possui SELECT COUNT(tbVeiculo.idMotorista) 'Quantidade de Veículos do Motorista com Código 1' FROM tbVeiculo	WHERE tbVeiculo.idMotorista = 1--Quantos veículos a placa começa com A  SELECT COUNT(tbVeiculo.idVeiculo) 'Quantidade de Veiculos que a placa começa com A' FROM tbVeiculo	WHERE tbVeiculo.placa LIKE 'A%'--Quantos veículos foram fabricados antes de 2010SELECT COUNT(tbVeiculo.anoVeiculo) 'Quantidade de Veiculos que Foram Fabricados antes de 2010' FROM tbVeiculo	WHERE tbVeiculo.anoVeiculo<2010--A média do ano de fabricação de todos os veículos SELECT AVG(tbVeiculo.anoVeiculo) 'Média do ano de Fabricação dos Veículos' FROM tbVeiculo--A quantidade dos veículos que tenham a palavra Sedan em seu modelo SELECT COUNT(tbVeiculo.modeloVeiculo) 'Quantidade de Veículos que Tenham a Palavra Sedan no Modelo' FROM tbVeiculo	WHERE tbVeiculo.modeloVeiculo LIKE 'Sedan'--A somatória dos pontosMulta do veículo de id 02 SELECT SUM(tbMultas.pontosMulta) 'Somatória dos Pontos de Multa do Veículo do ID 2' FROM tbMultas	WHERE tbMultas.idVeiculo = 2--A média de pontosMulta entre todas as multas aplicadas SELECT AVG(tbMultas.pontosMulta) 'Média de Pontos de Multa entre Todas as Multas Aplicadas' FROM tbMultas--A quantidade de multas aplicadas no mês de abril de 2018 SELECT COUNT(tbMultas.idMulta) 'Quantidade de Multas Aplicadas no Mês de Abril de 2018' From tbMultas	WHERE YEAR (tbMultas.dataMulta) = '2018' AND MONTH (tbMultas.dataMulta) = '4'--A quantidade de veículos cuja placa termine com o número 1 ou 2SELECT COUNT(tbVeiculo.idVeiculo) 'Quantidade de Veículos que terminam com o Final da Placa no Num 1 ou 2' FROM tbVeiculo	WHERE tbVeiculo.placa LIKE '%1' OR tbVeiculo.placa LIKE '%2'--A quantiadade de veículos cuja placa termine com o número 3 ou 4SELECT COUNT(tbVeiculo.idVeiculo) 'Quantidade de Veículos que terminam com o Final da Placa no Num 3 ou 4' FROM tbVeiculo	WHERE tbVeiculo.placa LIKE '%3' OR tbVeiculo.placa LIKE '%4'--A quantidade de veículos cuja placa termine com o número 5 ou 6SELECT COUNT(tbVeiculo.idVeiculo) 'Quantidade de Veículos que terminam com o Final da Placa no Num 5 ou 6' FROM tbVeiculo	WHERE tbVeiculo.placa LIKE '%5' OR tbVeiculo.placa LIKE '%6'--A quantidade de veículos cuja placa termine com 7 ou 8 SELECT COUNT(tbVeiculo.idVeiculo) 'Quantidade de Veículos que terminam com o Final da Placa no Num 7 ou 8' FROM tbVeiculo	WHERE tbVeiculo.placa LIKE '%7' OR tbVeiculo.placa LIKE '%8'--A quantidade de veículos cuja placa termine com 9 ou 0 SELECT COUNT(tbVeiculo.idVeiculo) 'Quantidade de Veículos que terminam com o Final da Placa no Num 9 ou 0' FROM tbVeiculo	WHERE tbVeiculo.placa LIKE '%9' OR tbVeiculo.placa LIKE '%0'--A quantidade de veículos por ano de fabricação SELECT COUNT(tbVeiculo.anoVeiculo) 'Quantidade de Veículos por Ano de Fabricação' FROM tbVeiculo--A quantidade de motoristas por pontuação acumulada SELECT COUNT(tbMotorista.pontuacaoAcumulada) 'Quantidade de Motoristas por Pontuação Acumulada' FROM tbMotorista--A quantidade de motoristas que ultrapassaram os 20 pontos SELECT COUNT(tbMotorista.pontuacaoAcumulada) 'Quantidade de Motoristas que Ultrapassaram os 20  Pontos na Carteira' FROM tbMotorista	WHERE tbMotorista.pontuacaoAcumulada>20--A pontuação média dos motoristas nascidos no ano de 2000SELECT AVG(tbMotorista.pontuacaoAcumulada) 'Pontuação Média dos Motoristas nascidos no Ano de 2000' FROM tbMotorista	WHERE YEAR (tbMotorista.dataNascimentoMotorista) = '2000'--A média dos pontos  das multas aplicadas em julho de 2017SELECT AVG(tbMultas.pontosMulta) 'Média de Pontos Aplicadas em Julho de 2017' FROM tbMultas
	WHERE YEAR(tbMultas.dataMulta) = 2017 AND MONTH(tbMultas.dataMulta) = 7--Quantos veículos não podem circular na segunda-feira (placa final 1 ou 2) SELECT COUNT(tbVeiculo.idVeiculo) 'Quantidade de Veículos que Não podem circular na Segunda Feira' FROM tbVeiculo	WHERE tbVeiculo.placa LIKE '%1' OR  tbVeiculo.placa LIKE '%2'	